<?php    /*    Template Name: Одиночная запись по умолчанию    */?><?php get_header(); ?>    <div id="content" class="block">	<?php if(!is_post_type_archive()){	if( has_term( $term, $taxonomy, $post ) != '' ){	if($taxonomy == 'categories') {	echo "<h1 class='aligncenter'>";	page_title();	echo "</h1>";	}}}		if(is_post_type_archive('portfolio')){			echo "<h1 class='aligncenter'>";			page_title();			echo "</h1>";		}		//print_r($post);		?>        <?php if (have_posts()) : 		while (have_posts()) : the_post(); ?>        <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>			<a class="portfolio-block" href="<?php the_permalink(); ?>">            					<div class="thumb">                    <?php the_post_thumbnail(); ?>                </div>				            	<div class="portfolio-content text-left">                					<div class="portfolio-title aligncenter">					<h2><?php the_title(); ?></h2>                </div>                    <?php the_excerpt(); ?>				</div>			</a>			</div>    <?php endwhile; endif; ?>				<div class="clear"></div>    </div><?php wp_reset_query(); ?><?php get_footer(); ?>