<?php/*Template Name: Одиночная запись по умолчанию*/?><?php get_header(); ?>    <div id="content" class="block">        <?php if (have_posts()) : 		while (have_posts()) : the_post(); ?>        <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<div class="portfolio-block text-left">                <div class="portfolio-title">					<h1><?php the_title(); ?></h1>					<span class="portfolio-date">						<?php the_time( get_option( 'date_format' ) ); ?>					</span>				</div>				<div class="portfolio-content">					<?php the_content(); ?>					</div>			<div class="clear"></div>			<p><?php edit_post_link ('<br /><p>[редактировать проект]</p>', '', ''); ?></p>			</div>    <?php endwhile; endif; ?>    </div><div class="clear"></div></div><?php get_footer(); ?>